INSERT  INTO `aa_main_duties`(`id`,`hrpid`,`desc`,`cby`,`uby`,`con`,`uon`) VALUES (1,1,'Pimpinan Pesantren (Mudir) adalah Kiyai/Khadimul Ma€™had yang diberi tugas memimpin pesantren yang diangkat dan diberhentikan oleh Pimpinan Yayasan',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(2,1,'Melindungi dan bertanggung jawab atas semua bagian dan kegiatan Pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(3,1,'Mendesain dan meningkatkan mutu pendidikan, pembelajaran dan kepengasuhan di pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(4,1,'Mendidik dan mengasuh warga pesantren serta menciptakan kehidupan pesantren yang kondusif',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(5,1,'Menjalin hubungan yang dinamis dengan stakeholders pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(6,1,'Menyupervisi, memonitoring dan mengevaluasi kinerja  semua kegiatan',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(7,1,'Memberi SK terhadap bagian-bagian penanggung jawab kegiatan pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(8,2,'Wakil Pimpinan Pesantren (Wakil Mudir) adalah Guru/Pengasuh yang bertugas membantu Pimpinan Pesantren dalam membina Organisasi Otonom (Ortom) di Pesantren dan membimbing kehidupan ke-Islaman yang diangkat dan diberhentikan oleh Yayasan',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(9,2,'Membantu mudir dalam mendesain dan meningkatkan mutu pendidikan, pembelajaran dan kepengasuhan di pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(10,2,'Membantu mudir dalam mendidik dan mengasuh warga pesantren serta menciptakan kehidupan pesantren yang kondusif',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(11,2,'Membantu mudir dalam menjalin hubungan yang dinamis dengan stakeholders pesantren',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(12,2,'Membantu mudir dalam menyupervisi, memonitoring dan mengevaluasi kinerja  semua kegiatan',1,1,'2021-12-10 10:00:00','2021-12-10 10:00:00'),(13,2,'Memimpin rap