/*Table structure for table `aa_person` */

DROP TABLE IF EXISTS `aa_person`;

CREATE TABLE `aa_person` (
  `id` bigint(11) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `nickname` varchar(255) DEFAULT NULL COMMENT 'Nama Panggilan',
  `name_ar` varchar(50) CHARACTER SET utf16 COLLATE utf16_unicode_ci DEFAULT NULL,
  `sex` char(1) DEFAULT NULL COMMENT 'p=Pria w=Wanita',
  `pob` varchar(50) DEFAULT NULL COMMENT 'Place of Birth',
  `dob` date DEFAULT NULL COMMENT 'Date of Birth',
  `son_order` tinyint(4) DEFAULT NULL COMMENT 'Anak ke',
  `siblings` tinyint(4) DEFAULT NULL COMMENT 'Jumlah Saudara kandung',
  `stepbros` tinyint(4) DEFAULT NULL COMMENT 'Jumlah Saudara Tiri',
  `adoptives` tinyint(4) DEFAULT NULL COMMENT 'Jumlah Saudara Angkat',
  `citizen` varchar(255) DEFAULT NULL COMMENT 'Kewarganegaraan',
  `religion` varchar(255) DEFAULT NULL COMMENT 'Agama',
  `languages` varchar(255) DEFAULT NULL COMMENT 'Bahasa',
  `user_id` int(11) DEFAULT NULL COMMENT 'Relasi dengan User',
  `ayah_id` int(11) DEFAULT NULL COMMENT 'Relasi dengan Person untuk Ayah',
  `ibu_id` int(11) DEFAULT NULL COMMENT 'Relasi dengan Person untuk Ayah',
  `wali_id` int(11) DEFAULT NULL COMMENT 'Relasi dengan Person untuk Ayah',
  `age` int(11) DEFAULT NULL COMMENT 'Umur',
  `job` varchar(255) DEFAULT NULL COMMENT 'Pekerjaan',
  `last_education` varchar(255) DEFAULT NULL COMMENT 'Pendidikan Terakhir',
  `income` decimal(12,2) DEFAULT NULL COMMENT 'Penghasilan',
  `stay_with_parent` tinyint(4) DEFAULT '1' COMMENT 'Tinggal bersama ortu',
  `height` int(11) NOT NULL DEFAULT '0' COMMENT 'Tinggi Badan',
  `weight` int(11) NOT NULL DEFAULT '0' COMMENT 'Berat Badan',
  `is_glasses` int(11) NOT NULL DEFAULT '0' COMMENT 'Berkacamata atau tidak',
  `character` varchar(255) DEFAULT NULL COMMENT 'Karakter',
  `hobbies` varchar(255) DEFAULT NULL COMMENT 'Hobi',
  `building_status` varchar(255) DEFAULT NULL COMMENT 'Status Rumah',
  `surface_area` varchar(255) DEFAULT NULL COMMENT 'Luas Tanah',
  `building_area` varchar(255) DEFAULT NULL COMMENT 'Luas Bangunan',
  `electricity_bills` decimal(12,2) DEFAULT NULL COMMENT 'Tagihan Listrik',
  `water_bills` decimal(12,2) DEFAULT NULL COMMENT 'Tagihan PDAM',
  `telecommunication_bills` decimal(12,2) DEFAULT NULL COMMENT 'Tagihan Telkom',
  `electronic` text COMMENT 'Alat Elektronik',
  `vehicle` text COMMENT 'Semua Kendaraan',
  `assets` text COMMENT 'Semua Aset',
  `cby` int(11) NOT NULL DEFAULT '0' COMMENT 'Created By = uid',
  `uby` int(11) NOT NULL DEFAULT '0' COMMENT 'Updated By = uid',
  `con` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Created On',
  `uon` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Updated On',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=294 DEFAULT CHARSET=latin1;

